import React from "react";
import "./App.css";
import Header from "./Header";
import Products from "./Products_List";
import Products_List from "./Products_List";
import { useState } from "react";

function App() {

    function doSomething(data) {
        console.log("Button is clicked! " + data);
    }

    // reading values from input
    let [name, setName] = useState();
    let [age, setAge] = useState();

    return (
        <div className="App">

            <div className="form">
                <h2>Enter details</h2>

                <input type="text" placeholder="Enter name" onChange={(event) => {
                setName(event.target.value);
                }}></input>

                <input type="number" placeholder="Enter Age" onChange={(event) => {
                setAge(event.target.value);
                }}></input>

                <h2>{name}</h2>
                <h2>{age}</h2>
            </div>



            {/* <Header/> */}
            {/* <Products_List/> */}
            {/* <button className="btn" onClick={(event) => {
                console.log(event);
                doSomething("Mihir");
            }}>mihir</button> */}
        </div>
    );
}

export default App;
