import React from "react";
import "./App.css";
import Header from "./Header";
import Products from "./Products_List";
import Products_List from "./Products_List";
import { useState } from "react";


function App() {

    let name = "Mihir";

    function changeName() {
        name = "John";
        console.log(name);
    }

    function doSomething(data) {
        console.log("Button is clicked! " + data);
    }

    return (
        <div>
            <Header/>
            <Products_List/>
            <button className="btn" onClick={(event) => {
                console.log(event);
                doSomething("Mihir");
            }}>Click</button>

            <h1>{name}</h1>
            <button className="btn" onClick={changeName}>Change Name</button>
        </div>
    );
}

export default App;
