import { useEffect, useState } from 'react';
import News from './News';
import './App.css';

function App() {

  let [articles, setArticles] = useState([]);

  useEffect(() => {

    fetch("https://newsapi.org/v2/everything?q=openai&from=2023-12-22&apiKey=4f8ca35a1f284d7a9e7dfd9b0c97958f")
    .then((response) => {
      return response.json();
    }).then((news) => {
      setArticles(news.articles);
    }).catch((err) => {
      console.log(err);
    });

  }, []);

  return (
    <div className="App">
      <h1>News App</h1>

      <section className='news-articles'>
        <News/>
        <News/>
        <News/>
        <News/>
      </section>

    </div>
  );
}

export default App;
