function Product() {

    let [name, setName] = useState("Mihir")

    useEffect(() => {
        console.log("component rendered")
    })

    return (
        <div className="product">
            <h1>Samsung A31</h1>
            <h2>{name}</h2>
            <button onClick={() => {
                setName("John");
            }}></button>
        </div>
    );

}

export default Product;
