import { useState } from "react";
import "./App.css";
import Product from "./Product";

function App() {

  let [productVisible, setProductVariable] = useState(false);

  return (
    <div className="App">

      <h1>App component</h1>

      <button onClick={() => {
        setProductVariable(true);
      }}>Show Product</button>

      <button onClick={() => {
        setProductVariable(false);
      }}>Hide Product</button>

      {
        productVisible == true
        ?(
          <Product/>
        )
        :null
      }

    </div>
  );
}

export default App;
